import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import Flag from "react-world-flags"; // for displaying flags
import { BsChevronDown } from "react-icons/bs"; // dropdown icon

const LanguageSwitcher = () => {
    const { i18n } = useTranslation();
    const [isOpen, setIsOpen] = useState(false); // state to toggle dropdown visibility

    const changeLanguage = (lang) => {
        i18n.changeLanguage(lang);
        setIsOpen(false); // Close dropdown after language change
    };

    return (
        <div className="relative">
            <button
                onClick={() => setIsOpen(!isOpen)} // Toggle dropdown
                className="flex items-center px-3 py-1 bg-gray-200 text-gray-700 rounded">
                {/* Selected Language (e.g., the flag or text) */}
                <span className="mr-2">Select Language</span>
                <BsChevronDown size={20} /> {/* Dropdown icon */}
            </button>

            {/* Dropdown menu */}
            {isOpen && (
                <div className="absolute right-0 mt-2 bg-white shadow-lg rounded-md w-32 py-2">
                    <button
                        onClick={() => changeLanguage("en")}
                        className="flex items-center px-4 py-2 hover:bg-gray-200 w-full">
                        <Flag code="GB" alt="English" className="w-5 h-5 mr-2" />
                        English
                    </button>
                    <button
                        onClick={() => changeLanguage("de")}
                        className="flex items-center px-4 py-2 hover:bg-gray-200 w-full">
                        <Flag code="DE" alt="German" className="w-5 h-5 mr-2" />
                        German
                    </button>
                </div>
            )}
        </div>
    );
};

export default LanguageSwitcher;
