import { useState } from "react";
import { Link } from "react-router-dom";
import navLinks from "../navLinks";
import { VscMenu, VscClose } from "react-icons/vsc";

const MobileNavbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return  (
    <>

<div className="bg-slate-100/85 backdrop-blur-sm max-w-full sticky top-0 flex flex-wrap p-2 space-x-8 items-center shadow-lg z-40">
{/* Hamburger Icon */}
<VscMenu size={30} onClick={() => setIsOpen(true)} />




</div>


      {/* Sidebar Drawer */}
      <div
        className={`fixed top-0 left-0 h-full w-64 bg-slate-100/85 backdrop-blur-sm transform ${
          isOpen ? "translate-x-0" : "-translate-x-full"
        } transition-transform duration-300 ease-in-out z-40 shadow-lg`}
      >
        {/* Close Button */}
        <VscClose size={25} onClick={() => setIsOpen(false)} className="absolute top-4 right-4"/>


        {/* Navigation Links */}
        <nav className="mt-12 p-4">
          <ul className="space-y-4">
            {navLinks.map(({ path, label }) => (
              <li key={path}>
                <Link
                  to={path}
                  className="block py-2 px-4 text-lg hover:bg-gray-700 rounded"
                  onClick={() => setIsOpen(false)} // Close menu on link click
                >
                  {label}
                </Link>
              </li>
            ))}
          </ul>
        </nav>
      </div>

      {/* Overlay (to close menu when clicking outside) */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black opacity-50 z-30"
          onClick={() => setIsOpen(false)}
        ></div>
      )}
    </>
  );
};

export default MobileNavbar;
