import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import enFlag from "../assets/flags/en.png";  // Path to English flag image
import deFlag from "../assets/flags/de.png";  // Path to German flag image
import { BsChevronDown } from "react-icons/bs";  // Dropdown icon

const LanguageSwitcher = () => {
    const { i18n } = useTranslation();
    const [isOpen, setIsOpen] = useState(false);  // State to toggle the dropdown visibility

    const changeLanguage = (lang) => {
        i18n.changeLanguage(lang);
        setIsOpen(false);  // Close dropdown after language change
    };

    return (
        <div className="relative">
            <button
                onClick={() => setIsOpen(!isOpen)} // Toggle dropdown visibility
                className="flex items-center px-3 py-1 bg-gray-200 text-gray-700 rounded z-40">
                <span className="mr-2">Select Language</span>
                <BsChevronDown size={20} /> {/* Dropdown icon */}
            </button>

            {/* Dropdown menu */}
            <div className={`fixed top-0 right-0 mb-2 bg-white shadow-lg rounded-md w-32 py-2 bg-slate-50/85 backdrop-blur-sm transform 
               ${isOpen ? "opacity-100 translate-y-0 z-50" : "opacity-0 translate-y-full z-0"}    // Adjust z-index based on visibility
                transition-transform duration-700 ease-in-out`}>
                <button
                    onClick={() => changeLanguage("en")}
                    className="flex items-center px-4 py-2 hover:bg-gray-200 w-full">
                    <img
                        src={enFlag}
                        alt="English"
                        className={`w-7 h-5 rounded-sm mr-2 ${i18n.language === "en" ? "" : "opacity-65"}`}
                    />
                    English
                </button>
                <button
                    onClick={() => changeLanguage("de")}
                    className="flex items-center px-4 py-2 hover:bg-gray-200 w-full">
                    <img
                        src={deFlag}
                        alt="German"
                        className={`w-7 h-5 rounded-sm mr-2 ${i18n.language === "de" ? "" : "opacity-65"}`}
                    />
                    German
                </button>
            </div>
        </div>
    );
};

export default LanguageSwitcher;
