import { Link } from "react-router-dom";
import logo from '../logo.png';
import LanguageSwitcher from "./LanguageSwitcher";
import { useTranslation } from "react-i18next";
import "../i18n";

import navLinks from "../navLinks";

const DesktopNavbar = () => {
  const { t } = useTranslation();

  return (
    <div className="bg-slate-50/85 backdrop-blur-sm max-w-full sticky top-0 flex flex-wrap space-x-8 items-center shadow-lg z-40">
      <img src={logo} className="max-w-20" alt="logo" />

      {navLinks.map((link, index) => {
  const translationKey = `nav.${link.label}`; // Dynamically create the key
  const translatedLabel = t(translationKey, { defaultValue: link.label }); // Get the translation
  return (
    <li key={index}>
      <Link
        to={link.path}
        className="block py-2 px-4 text-lg font-mono font-semibold no-underline hover:text-sky-950 hover:italic"
        onClick={() => setIsOpen(false)} // Close menu on link click
      >
        {translatedLabel}  {/* Use the translated label here */}
      </Link>
    </li>
  );
})}

      <LanguageSwitcher />
    </div>
  );
};

export default DesktopNavbar;
