import { useState, useEffect } from "react";

const TypingEffect = ({ words, speed = 100, delay = 1000, disableSpaces = false }) => {
    const [text, setText] = useState("");
    const [wordIndex, setWordIndex] = useState(0);
    const [charIndex, setCharIndex] = useState(0);

    useEffect(() => {
        setText(""); // Reset text when words change
        setWordIndex(0);
        setCharIndex(0);
      }, [words]); // This ensures it works multiple times on the page

    useEffect(() => {
        if (wordIndex < words.length) {
            if (charIndex < words[wordIndex].length) {
                const timeout = setTimeout(() => {
                    setText((prev) => prev + words[wordIndex][charIndex]); // Append each letter
                    setCharIndex(charIndex + 1);
                }, speed);
                return () => clearTimeout(timeout);
            } else {
                setTimeout(() => {
                    setWordIndex(wordIndex + 1);
                    setCharIndex(0);
                    setText((prev) => prev + (disableSpaces ? "" : " ")); // Add space between words
                }, delay);
            }
        }
    }, [charIndex, wordIndex, words, speed, delay, disableSpaces]);

    return <span>{text}</span>;
};

export default TypingEffect;
