import { useState, useEffect } from "react";

const TypingEffect = ({ words, speed = 100, delay = 1000, initialDelay = 2000, disableSpaces = false, onComplete }) => {
    const [displayedText, setDisplayedText] = useState(""); 
    const [currentWord, setCurrentWord] = useState(""); 
    const [wordIndex, setWordIndex] = useState(-1); // Start with -1 to wait for initial delay
    const [charIndex, setCharIndex] = useState(0);
    const [startTyping, setStartTyping] = useState(false);

    useEffect(() => {
        const timer = setTimeout(() => {
            setStartTyping(true);
            setWordIndex(0);
        }, initialDelay);
        return () => clearTimeout(timer);
    }, [initialDelay]);

    useEffect(() => {
        if (!startTyping || wordIndex >= words.length) return;

        if (charIndex < words[wordIndex].length) {
            const timeout = setTimeout(() => {
                setCurrentWord((prev) => prev + words[wordIndex][charIndex]);
                setCharIndex(charIndex + 1);
            }, speed);
            return () => clearTimeout(timeout);
        } else {
            setTimeout(() => {
                setDisplayedText((prev) => prev + currentWord + (disableSpaces ? "" : " "));
                setCurrentWord("");
                if (wordIndex === words.length - 1) {
                    onComplete?.();
                } else {
                    setWordIndex(wordIndex + 1);
                    setCharIndex(0);
                }
            }, delay);
        }
    }, [charIndex, wordIndex, words, speed, delay, disableSpaces, onComplete, currentWord, startTyping]);

    return <span>{displayedText + currentWord}</span>;
};

export default TypingEffect;
