import { useState, useEffect } from "react";

const ExpandingLine = ({ minWidth = 50, maxWidth = 200, speed = 500, className = "" }) => {
    const [, setWidth] = useState(minWidth);
    const [growing, setGrowing] = useState(true);

    useEffect(() => {
        const interval = setInterval(() => {
            setWidth((prevWidth) => {
                if (growing && prevWidth >= maxWidth) {
                    setGrowing(false);
                    return prevWidth - 10;
                } else if (!growing && prevWidth <= minWidth) {
                    setGrowing(true);
                    return prevWidth + 10;
                }
                return growing ? prevWidth + 10 : prevWidth - 10;
            });
        }, speed);

        return () => clearInterval(interval);
    }, [growing, minWidth, maxWidth, speed, className]);

    return (
        <div style={{
            width: `${width}px`,
            transition: `width ${speed / 1000}s linear`
        }} 
        className={className}/>
    );
};

export default ExpandingLine;
